import numpy as npimport matplotlib.pyplot as plt############# PARAMETRES############pas = 200 # nombre de jours entre deux positions successivesn   = 8  # nombre de  positions représentées sur le graphique############################################################################ LECTURE DES FICHIERS DE DONNÉES POUR RÉCUPÉRER LES POSITIONS DES PLANÈTES###########################################################################data = np.loadtxt("finlay.txt", skiprows=8)x = np.array([data[i*pas,0] for i in range(n) if i*pas<len(data)])y = np.array([data[i*pas,1] for i in range(n) if i*pas<len(data)])############################################################### REPRESENTATION GRAPHIQUE DES POSITIONS DES DIFFÉRENTS OBJETS##############################################################fig = plt.figure()plt.plot([0],[0], "o", color="gold", label="Soleil")plt.plot(x, y, "o", color="red", label="Comète")plt.plot([x[0]], [y[0]], "oC1")plt.plot([x[-1]], [y[-1]], "oC2")# Changement d'échelle pour les axes (modification des limites du graphe)#plt.xlim(-7,2)#plt.ylim(-3,3)# Titre, nom des axes et légendeplt.title("Évolution de la position de la comète")plt.xlabel("Abscisse par rapport au soleil\nen unités astronomiques (au)")plt.ylabel("Ordonnée par rapport au soleil\nen unités astronomiques (au)")plt.legend(loc = "upper left")fig.axes[0].set_aspect("equal")plt.show()